!function(t){"use strict"}(jQuery);
$('.btn-secondary').on('click',()=>{
    var searchTitle = $('.form-control').val();
    console.log(searchTitle);
    $.ajax({
        method: 'GET',
        url: 'https://omdbapi.com/?s=' + searchTitle + '&apikey=2f6435d9',
        dataType: 'json',
    }).done(onSuccess);
  })

  function onSuccess(responseData) {
    $('.list-unstyled').empty();
    console.log(responseData["Search"]);
    responseData["Search"].forEach(movie => {
        var a = document.createElement('a')
        var li = document.createElement('li')
        $(a).html(movie["Title"]);
        $(a).attr("href", "https://www.imdb.com/title/" + movie["imdbID"] + "/")
        $(a).attr("target","_blank");
        $(a).addClass("list-unstyled-item");
        li.prepend(a);
        $('.list-unstyled').prepend(li);
    });
  };